import react, { useEffect, useState } from 'react';
import {getQuote} from '../services';

export const QuoteMaker = (props) => {
    let [quote, setQuote] = useState('random quote');
    let [generating, setGenerating] = useState(false);

    useEffect(() => {
        if (generating) {
            const data = getQuote();
            console.log(data);
            setQuote(data.content);
            setGenerating(false);
        }

    }, []);

    const generateQuote = () => {
        console.log("generating");
        setGenerating(true);
    }

    return(
        <div>
            <label>{quote}</label>
            <br />
            <br />
            <button onClick={generateQuote}>Generate</button>
        </div>
    )
}
