import React from 'react';
import {useState, useEffect} from 'react';

export const Counter = (props) => {

    let [count, setCount] = useState(0);
    let visibility = true;

    function handlerUp() {
        setCount(++count);
        visibility = false;
      }

      function reset() {
        setCount(0);
        visibility = false;
      }

      function handlerDown() {
        setCount(--count);
        visibility = false;
      }
    return (
        <div>
            <p>This is a simple demonstration of a button: {count}</p>
            <ul>
            <li><button size="sm" onClick={handlerUp}>Going Up</button></li>
            <li><button onClick={() => setCount(handlerDown)}>Going Down</button></li>
            <li><button onClick={() => setCount(reset)}>Reset</button></li>
            </ul>
        </div>
    );
}
