import logo from './logo.svg';
import React from 'react';
import {useState, useEffect} from 'react';
import './App.css';

function App() {
  let [count, setCount] = useState(0);

  // useEffect((props) => {
  //   setCount(count++);
  // });

  function handlerUp() {
    setCount(count++);
  }

  function reset() {
    setCount(0);
  }

  function handlerDown() {
    setCount(count--);
  }

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>This is a simple demonstration of a button: {count}</p>
        <ul>
        <li><button onClick={() => setCount(++count)}>Going Up</button></li>
        <li><button onClick={() => setCount(--count)}>Going Down</button></li>
        <li><button onClick={() => setCount(0)}>Reset</button></li>
        </ul>

        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
