import React from 'react';
import {useState, useEffect} from 'react';

export const Counter = (props) => {

    let [count, setCount] = useState(props.init);

    function handlerUp() {
        setCount(++count);
      }

      function reset() {
        setCount(0);
      }

      function handlerDown() {
        setCount(--count);
      }
    return (
        <div>
            <p>This is a simple demonstration of a button: {count}</p>
            <ul>
            <li><button size="sm" onClick={handlerUp}>Going Up</button></li>
            <li><button onClick={() => setCount(--count)}>Going Down</button></li>
            <li><button onClick={() => setCount(0)}>Reset</button></li>
            </ul>
        </div>
    );
}
